import os

import numpy as np
from scipy import stats
from parameters import ROOT_DIR
from utils import load_txt, save_data

dataset = os.path.join(ROOT_DIR, 'datasets/class_boxes')


def get_class_stat(dataset: str, lenth: int = 3):
    # print(os.listdir(dataset))
    classes = os.listdir(dataset)

    stat = {'camera_1': {}, 'camera_2': {}}
    for cam in ['camera_1', 'camera_2']:
        class_1 = os.path.join(dataset, classes[0], cam)
        class_1_cont = os.listdir(class_1)
        class_1_cont = sorted(class_1_cont)
        for cl in classes:
            # stat[cam][cl] = {}
            class_1 = os.path.join(dataset, cl, cam)
            class_1_cont = os.listdir(class_1)
            class_sq, class_dia = [], []
            for vid in class_1_cont:
                sq, dia = [], []
                for box in os.listdir(os.path.join(class_1, vid)):
                    if load_txt(os.path.join(class_1, vid, box)):
                        coord = load_txt(os.path.join(class_1, vid, box))[0].split(',')
                        coord = [float(x) for x in coord]
                        square = (coord[2] - coord[0]) * (coord[3] - coord[1])
                        diagonal = ((coord[2] - coord[0]) ** 2 + (coord[3] - coord[1]) ** 2) ** 0.5
                        sq.append(square)
                        dia.append(diagonal)
                        # print(box, square, diagonal)
                if sq:
                    z = np.abs(stats.zscore(sq))
                    zd = np.abs(stats.zscore(dia))
                    out = np.where(z > 1)[0]
                    outd = np.where(zd > 1)[0]
                    r = list(range(len(sq)))
                    rd = list(range(len(dia)))
                    for idx in out:
                        r.pop(r.index(idx))
                    for idx in outd:
                        rd.pop(rd.index(idx))
                    sq = np.array(sq)[r]
                    dia = np.array(dia)[rd]
                    x = list(zip(sq, list(range(len(sq)))))
                    x = sorted(x, reverse=False)
                    x = x[:lenth]
                    x = [i[1] for i in x]
                    sq = np.array(sq)[x].tolist()

                    x = list(zip(dia, list(range(len(dia)))))
                    x = sorted(x, reverse=False)
                    x = x[:lenth]
                    x = [i[1] for i in x]
                    dia = np.array(dia)[x].tolist()
                while len(sq) < lenth:
                    sq.append(0)
                while len(dia) < lenth:
                    dia.append(0)
                class_sq.append(sq)
                class_dia.append(dia)
            stat[cam][cl] = {'square': class_sq, 'diagonal': class_dia}
    return stat


stat = get_class_stat(dataset, 3)
# print(stat)
save_data(stat, file_path=os.path.join(ROOT_DIR, 'tests'), filename='class_stat')

